import { DataGrid } from '@mui/x-data-grid';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { useNavigate } from 'react-router';
import axios from 'axios';
import Toast from '../toast/toast';
import { useState } from 'react';

const theme = createTheme();

export default function SignIn({isLoggedIn, setIsLoggedIn}) {
  const navigate = useNavigate();
  const [toastOpen, setToastOpen] = useState(false);
  const [toastMessage, setToastMessage] = useState('');
  const [toastSeverity, setToastSevertiy] = useState('');
  const [rows, setRows] = useState(rows);
  const [columns, setColumns] = useState(columns);


  const handleSubmit = (event) => {
    event.preventDefault();
    const data = new FormData(event.currentTarget);
    var email = data.get('email');
    var password = data.get('password');

    axios.post('http://localhost:4000/api/tickets', {
        email: data.get('email'),
        password: data.get('password'),
    })
      .then(res => {
        console.log(res.data);
        localStorage.setItem('token', res.data.token);
        setIsLoggedIn(true);
        setToastOpen(true);
        setToastMessage('Successfully Logged In');
        setToastSevertiy('success');
        setTimeout(() => {  navigate("/dashboard"); }, 1000);

      })
      .catch(error => {
        console.log(error);
        setToastOpen(true);
        setToastMessage('Email Password is not matching!');
        setToastSevertiy('error');
      });
    }

  return (
    <>
      <DataGrid dataSource={rows}
        id='gridContainer'
        keyExpr="id"
        width="95%"
        height="calc(100vh - 120px)"
        showColumnLines={true}
        showRowLines={true}
        showBorders={true}
        rowAlternationEnabled={true}
        columns={columns}
        >
          <SearchPanel visible={true} highlightCaseSensitive={true} />
          <Pager allowedPageSizes={pageSizes} showPageSizeSelector={true} />
          <Paging defaultPageSize={25} />
          <Export enabled={true} />
      </DataGrid>
    </>
  );
}
