import { useLocation } from "react-router";
import { Box, Typography, Grid, Avatar, Paper } from "@mui/material";
import dateformat from 'dateformat';
import { deepOrange } from "@mui/material/colors";
import { brown, orange } from "@mui/material/colors";

const TicketDetails = () => {
    const { state } = useLocation();
    const { ticket } = state;
    console.log(ticket);
    return (
        <div sx={{backgroundColor: orange[100]}}>
            <Paper elevation={2} sx={{textAlign: 'left', backgroundColor: orange[300]}}>
                <Typography sx={{color: "white", display:"flex"}} variant="h7" margin="10px">
                    Ticket Number #{ticket.id}
                </Typography>
                <Typography sx={{color: "white"}} variant="h7" margin="10px">
                    Status {ticket.status}
                </Typography>
            </Paper>
            <Paper elevation={2} sx={{textAlign: 'left', backgroundColor: orange[300]}}>
                <Typography sx={{color: "white"}} variant="h5" margin="10px">
                    {ticket.subject}
                </Typography>
            </Paper>
            <Paper elevation={2} sx={{background:orange[300]}}>
                <Grid container>
                    <Grid xs={1} sx={{textAlign:'center'}}>
                        <Avatar sx={{ bgcolor: deepOrange[500], mx: 'auto', my: '5px'}} 
                            alt={ticket.requester} src="/static/images/avatar/1.jpg" />
                    </Grid>
                    <Grid xs={11}>
                        <Box>
                            <Grid container>
                                <Grid xs={6}>
                                    <Typography sx={{color: "white"}} textAlign="left">{ticket.requester}</Typography>
                                </Grid>
                                <Grid xs={6}>
                                    <Typography sx={{color: "white"}} textAlign="right">{dateformat(ticket.updated_at)}</Typography>
                                </Grid>
                            </Grid>
                        </Box>

                        <Typography sx={{color: "white", height: "70vh"}} textAlign="left">
                            {ticket.description}
                        </Typography>
                    </Grid>
                </Grid>
            </Paper>
        </div>
    );
};

export default TicketDetails;
