import { useLocation } from "react-router";
import { Box, Typography, Grid, Avatar } from "@mui/material";
import dateformat from 'dateformat';
import { deepOrange } from "@mui/material/colors";


const Item = styled(Paper)(({ theme }) => ({
    ...theme.typography.body2,
    textAlign: 'center',
    color: theme.palette.text.secondary,
    height: 60,
    lineHeight: '60px',
}));

const TicketDetails = () => {
    const { state } = useLocation();
    const { ticket } = state;
    console.log(ticket);
    return (
        <div>
            <Item sx={{textAlign: 'left', border: 1, borderColor: 'grey' }}>
                <Typography variant="h5" margin="10px">
                    {ticket.subject}
                </Typography>
            </Item>
            <Box>
                <Grid container>
                    <Grid xs={1} sx={{textAlign:'center'}}>
                        <Avatar sx={{ bgcolor: deepOrange[500], mx: 'auto', my: '5px'}} 
                            alt={ticket.requester} src="/static/images/avatar/1.jpg" />
                    </Grid>
                    <Grid xs={11}>
                        <Box>
                            <Grid container>
                                <Grid xs={6}>
                                    <Typography textAlign="left">{ticket.requester}</Typography>
                                </Grid>
                                <Grid xs={6}>
                                    <Typography textAlign="right">{dateformat(ticket.updated_at)}</Typography>
                                </Grid>
                            </Grid>
                        </Box>

                        <Typography textAlign="left">
                            {ticket.description}
                        </Typography>
                    </Grid>
                </Grid>
            </Box>
        </div>
    );
};

export default TicketDetails;
