import { DataGrid } from '@mui/x-data-grid';
import { createTheme } from '@mui/material/styles';
import { useNavigate } from 'react-router';
import axios from 'axios';
import Toast from '../toast/toast';
import { useState, useEffect } from 'react';


const theme = createTheme();

export default function Tickets() {
  const navigate = useNavigate();
  const [toastOpen, setToastOpen] = useState(false);
  const [toastMessage, setToastMessage] = useState('');
  const [toastSeverity, setToastSevertiy] = useState('');
  const [rows, setRows] = useState(rows);
  const [columns, setColumns] = useState(columns);


  const getData = async () => {
    await axios.post('http://localhost:4000/api/tickets')
      .then(res => {

        setToastOpen(true);
        setToastMessage('Successfully Loaded Tickets');
        setToastSevertiy('success');

      })
      .catch(error => {
        console.log(error);
        setToastOpen(true);
        setToastMessage('Email Password is not matching!');
        setToastSevertiy('error');
      });
    }

  useEffect(() => {
    getData();
  }, []);

  return (
    <>
      <Toast open={toastOpen} setOpen={setToastOpen} message={toastMessage} severity={toastSeverity} />
      <DataGrid
        rows={rows}
        columns={columns}
        pageSize={15}
        rowsPerPageOptions={[15]}
        checkboxSelection
      />
    </>
  );
}
